import React from 'react'
import './AddMaterial.css'
import OnBoarding from '../../SmartContracts/OnBoarding/OnBoarding'
import { UserNav } from '../../component/Navbar/homeNav'
import { useState } from 'react'
import Select from 'react-select'

export default function AddMaterials() {


  const [materiale,SetMateriale] =useState([]);
  const [colors,SetColor] = useState([])

  const material=[
    {value:"0" , label:"ABS"},
    {value:"1",label:"PLA"},
    {value:"2",label:"PETG"}]  
    
  const color=[
    {value:"0", label:"NONE"},
    {value:"1", label:"BLACK"},
    {value:"2", label:"WHITE"},
    {value:"3", label:"BROWN"},
    {value:"4",label:"GRAY"},
    {value:"5", label:"YELLOW"},
    {value:"6", label:"ORANGE"},
    {value:"7", label:"RED"},
    {value:"8", label:"PINK"},
    {value:"9", label:"PURPLE"},
    {value:"10", label:"BLU"},
    {value:"11", label:"GREEN"}]

    const handleChangeMats=(option)=>{
      let values=[]
      option.map((value)=>{
          values.push(parseInt(value.value));
          
      })
      SetMateriale(values);
      

  }


    async function addMaterial(){
        const onboarding = new OnBoarding();
        var material = await onboarding.addMaterial();
    }

  return (
    <div>
        <div className='sign'>
            <UserNav/>
            <h1>Add Materials</h1>

            <div className="table" >
                <label>
                    <p>Name</p>
                    <input type="text" name="name"></input>
                </label><br/>

                <label>
                  <p>Material Type</p>
                  <div style={{
                    width:"200px",
                    margin:"auto"}}>
                      <Select isMulti
                      options={material}
                      getOptionValue={(option)=>option.value}
                      onChange={(option)=>{handleChangeMats(option);
                      }}
                      />
                  </div>
                </label>

                <label>
                  <p>Color</p>
                  <div style={{
                    width:"200px",
                    margin:"auto"}}>
                      <Select isMulti 
                      options={color}
                      getOptionValue={(option)=>option.value}
                      onChange={(option)=>{handleChange(option);
                      }}
                      />

                  </div>
                </label>

                <label>
                  <p>Quantity KG</p>
                  <input type="number"></input>
                </label>

                <label>
                  <p>Print Temp</p>
                  <input type="number"></input>
                </label>

                <label>
                  <p>Bed Temp</p>
                  <input type="number"></input>
                </label>


            </div>

        </div>
        <div>
            
        </div>
    </div>
  )
}
