                      }}
                      />
*/

const handleChangeState=(option)=>{
    let values=[]
    option.map((value)=>{
        values.push(parseInt(value.value));
        
    })
    SetStatus(values);
    console.log(values)
}

const handleSubmit = async(event)=>{
    event.preventDefault();
    //let stateobj = {name: Name, materiale: materiale, colors: colors,quantityKG: quantityKG, printTemp: printTemp, bedTemp: bedTemp}
    //console.log(stateobj)
    var vote = await voting.announce();
    console.log(vote)
    }

  return (
    <div className='sign'>
        <UserNav/>

        <h1>Add Design</h1>
        <div className='SignIn'>
            
            <label>
                <p>FileHash</p>
                <input type="text" placeholder='filehash' onChange={(event)=> SetHash(event.target.value)}/>
            </label>

            <label>
                <p>taup</p>
                <input type="number" placeholder='taup'/>
            </label>

            <label>
                <p>taur</p>
                <input type="number" placeholder='taur'/>
            </label>

           <button className="printerButton" onClick={handleSubmit}>Add Design</button>



        </div>

    </div>
  )
}
