import React, { useState } from 'react'
import './UpdateMaterial.css'
import OnBoarding from '../../SmartContracts/OnBoarding/OnBoarding'
import { UserNav } from '../../component/Navbar/homeNav'
import { useParams } from 'react-router-dom'
import { useEffect } from 'react'
import Select from 'react-select';
import Web3 from 'web3'


export default function UpdateMaterial() {

  let {id} = useParams()

const[name,Setname] = useState("");
const[type,Settype] = useState([]);
const[quantityKG,SetquantityKG] = useState(0);
const[printTemp,SetprintTemp] = useState(0);
const[bedTemp,SetBedTemp] = useState(0);

const [Material,SetMaterial] = useState([]);

const handleChange=(option)=>{
  let values=[]
  option.map((value)=>{
      values.push(parseInt(value.value));
      
  })
  Settype(values);
}



useEffect(() => {
  //console.log(id)
  async function getMat(){
  const onboard = new OnBoarding();
  var mate = await onboard.getMaterials()
  //let mats = mate[id]
  //console.log(mats)
  console.log(mate)
  SetMaterial(mate)
  console.log(mate[0])
  }
  getMat();
},[])

const material=[
  {value:"0" , label:"ABS"},
  {value:"1",label:"PLA"},
  {value:"2",label:"PETG"}]  
  
  const color=[
    {value:"0", label:"NONE"},
    {value:"1", label:"BLACK"},
    {value:"2", label:"WHITE"},
    {value:"3", label:"BROWN"},
    {value:"4",label:"GRAY"},
    {value:"5", label:"YELLOW"},
    {value:"6", label:"ORANGE"},
    {value:"7", label:"RED"},
    {value:"8", label:"PINK"},
    {value:"9", label:"PURPLE"},
    {value:"10", label:"BLU"},
    {value:"11", label:"GREEN"}]



async function UpdateMats(){
  const onboarding = new OnBoarding();
  var mats = await onboarding.updateMaterial();

}


  return (
    <div className='sign'>
        <UserNav/>
        <h1>UpdateMaterial</h1>
        <div className='SignIn'>

          <p>Name</p>
          <input type="text" placeholder={Material[0]} onChange={(e)=>{Setname(e)}}></input>
          <p>Material Type</p>
          <div style={{
                        width:"200px",
                        margin:"auto"}}>
                   <Select  isMulti 
                    options={material}
                    getOptionValue={(option)=>option.value}
                    onChange={(option)=>{handleChange(option);
                    console.log(option);}}/>
                    </div>
          <p>Color</p>
          <div style={{
                    width:"200px",
                    margin:"auto"}}>
                      <Select isMulti 
                      options={color}
                      getOptionValue={(option)=>option.value}
                      />

                  </div>
          <p>QuantityKg</p>  
          <input type="number" placeholder={Material.quantityKG} onChange={(e)=>{SetquantityKG(e)}}></input>
          <p>PrintTemp</p>
          <input type="number" placeholder={Material.printTemperature} onChange={(e)=>{SetprintTemp(e)}}></input>
          <p>bedTemp</p>
          <input type="number" onChange={(e)=>{SetBedTemp(e)}}></input><br/>
          <button className='next1'>Update Material</button>
        </div>
    </div>
  )
}
