    <div className='sign'>
        <UserNav/>

        <h1>Add Design</h1>
        <div className='SignIn'>
            
            <label>
                <p>FileHash</p>
                <input type="text" placeholder='filehash'/>
            </label>

            <label>
                <p>Vendor</p>
                <input type="text" placeholder='Address'/>
            </label>

            <label>
                <p>Balance</p>
                <input type="number" placeholder='balance'/>
            </label>

            <label>
                <p>Manager</p>
                <input type="text" placeholder='Address'/>
            </label>

            <label>
                <p>StatusPhase</p>
                <Select isMulti
                      options={stating}
                      getOptionValue={(option)=>option.value}
                      onChange={(option)=>{handleChangeState(option);
                      }}
                      />
            </label>

            <label>
                <p>taup</p>
