            </label>

            <label>
                <p>taur</p>
                <input type="number" placeholder='taur'/>
            </label>

            <label>
                <p>deltaExp</p>
                <input type="number" placeholder='deltaExp'/>
            </label>

            <label>
                <p>deltaReveal</p>
                <input type="number" placeholder='deltaReveal'/>
            </label>

            <label>
                <p>Result 1</p>
            </label>

            <label>
                <p>phase</p>
            </label>
            <button className="printerButton">Add Design</button>



        </div>

    </div>
  )
}
