import React, { useState } from 'react'
import './UpdateMaterial.css'
import OnBoarding from '../../SmartContracts/OnBoarding/OnBoarding'
import { UserNav } from '../../component/Navbar/homeNav'
import { useParams } from 'react-router-dom'
import { useEffect } from 'react'
import Select from 'react-select';


export default function UpdateMaterial() {

  let {id} = useParams()

const[name,Setname] = useState("");
const[type,Settype] = useState([]);
const[quantityKG,SetquantityKG] = useState(0);
const[printTemp,SetprintTemp] = useState(0);
const[bedTemp,SetBedTemp] = useState(0);

const [Material,SetMaterial] = useState([]);

const handleChange=(option)=>{
  let values=[]
  option.map((value)=>{
      values.push(parseInt(value.value));
      
  })
  Settype(values);
}



useEffect(() => {
  console.log(id)
  async function getMat(){
  const onboard = new OnBoarding();
  var mate = await onboard.getMaterials()
  let mats = mate[id]
  console.log(mats.name)
  return mats
  }
  SetMaterial(getMat());
},[])

const material=[
  {value:"0" , label:"ABS"},
  {value:"1",label:"PLA"},
  {value:"2",label:"PETG"}]  
  


async function UpdateMats(){
  const onboarding = new OnBoarding();
  var mats = await onboarding.updateMaterial();

}


  return (
    <div className='sign'>
        <UserNav/>
        <h1>UpdateMaterial</h1>
        <div className='SignIn'>

          <p>Name</p>
          <input type="text" placeholder={Material.name} onChange={(e)=>{Setname(e)}}></input>
          <p>Material Type</p>
          <div style={{
                        width:"200px",
                        margin:"auto"}}>
                   <Select  isMulti 
                    options={material}
                    getOptionValue={(option)=>option.value}
                    onChange={(option)=>{handleChange(option);
                    console.log(option);}}/>
                    </div>
          <p>Color</p>
          <p>QuantityKg</p>
          <p>PrintTemp</p>
          <p>bedTemp</p>
        </div>
    </div>
  )
}
