import React from 'react'
import './AddMaterial.css'
import OnBoarding from '../../SmartContracts/OnBoarding/OnBoarding'
import { UserNav } from '../../component/Navbar/homeNav'
import { useState } from 'react'
import Select from 'react-select'
import { useEffect } from 'react'

export default function AddMaterials() {


  const [materiale,SetMateriale] =useState([]);
  const [colors,SetColor] = useState([]);
  const [Name,Setname] = useState("");
  const [quantityKG,SetquantityKG] = useState(0);
  const [printTemp,SetprintTemp] = useState(0);
  const [bedTemp,SetBedTemp] = useState(0);
  const [section,setSection]=useState(1);

  let onBoarding=new OnBoarding();


  
  const material=[
    {value:"0" , label:"ABS"},
    {value:"1",label:"PLA"},
    {value:"2",label:"PETG"}]  
    
  const color=[
    {value:"0", label:"NONE"},
    {value:"1", label:"BLACK"},
    {value:"2", label:"WHITE"},
    {value:"3", label:"BROWN"},
    {value:"4",label:"GRAY"},
    {value:"5", label:"YELLOW"},
    {value:"6", label:"ORANGE"},
    {value:"7", label:"RED"},
    {value:"8", label:"PINK"},
    {value:"9", label:"PURPLE"},
    {value:"10", label:"BLU"},
    {value:"11", label:"GREEN"}]

    const handleChangeMats=(option)=>{
      let values=[]
      option.map((value)=>{
          values.push(parseInt(value.value));
          
      })
      SetMateriale(values);
      console.log(values)
  }

  const handleChangeColor=(option)=>{
    let values=[]
    option.map((value)=>{
        values.push(parseInt(value.value));
        
    })
    SetColor(values);
    console.log(values)
}

const handleSubmit = async(event)=>{
  event.preventDefault();
  let matobj = {Name: Name, materiale: materiale, colors: colors,quantityKG: quantityKG, printTemp: printTemp, bedTemp: bedTemp}
  console.log(matobj)
  //await onBoarding.addMaterial(matobj)
}



  return (
    <div>
        <div className='sign'>
            <UserNav/>
            <h1>Add Materials</h1>

            <div className="SignIn" >
                <label>
                    <p>Name</p>
                    <input type="text" onChange={event =>{Setname(event.target.value)}}></input>
                </label><br/>

              <div>
                <label>
                  <p>Material Type</p>
                  <div style={{
                    width:"200px",
                    margin:"auto"}}>
                      <Select isMulti
                      options={material}
                      getOptionValue={(option)=>option.value}
                      onChange={(option)=>{handleChangeMats(option);
                      }}
                      />
                  </div>
                </label>

                <label>
                  <p>Color</p>
                  <div style={{
                    width:"200px",
                    margin:"auto"}}>
                      <Select isMulti 
                      options={color}
                      getOptionValue={(option)=>option.value}
                      onChange={(option)=>{handleChangeColor(option);
                      }}
                      />

                  </div>
                </label>
              </div>
                <label>
                  <p>Quantity KG</p>
                  <input type="number" onChange={event => {SetquantityKG(event.target.value)}}></input>
                </label>

                <label>
                  <p>Print Temp</p>
                  <input type="number" onChange={event => {SetprintTemp(event.target.value)}}></input>
                </label>

                <label>
                  <p>Bed Temp</p>
                  <input type="number" onChange={event => {SetBedTemp(event.target.value)}}></input>
                </label><br/>
                <button className='next1' onClick={handleSubmit}>Try</button>


            </div>

        </div>
        <div>
            
        </div>
    </div>
  )
}
