import React from 'react'
import './AddMaterial.css'
import OnBoarding from '../../SmartContracts/OnBoarding/OnBoarding'
import { UserNav } from '../../component/Navbar/homeNav'
import { useState } from 'react'
import Select from 'react-select'

export default function AddMaterials() {


  const [materiale,SetMateriale] =useState([]); 

  const material=[
    {value:"0" , label:"ABS"},
    {value:"1",label:"PLA"},
    {value:"2",label:"PETG"}]    

    const handleChange=(option)=>{
      let values=[]
      option.map((value)=>{
          values.push(parseInt(value.value));
          
      })
      SetMateriale(values);
      if(values.length===0)
          printerData['material']=undefined;
      console.log(printerData['material']);
      

  }


    async function addMaterial(){
        const onboarding = new OnBoarding();
        var material = await onboarding.addMaterial();
    }

  return (
    <div>
        <div className='sign'>
            <UserNav/>
            <h1>Add Materials</h1>

            <div className="table" >
                <label>
                    <p>Name</p>
                    <input type="text" name="name"></input>
                </label><br/>

                <label>
                  <p>Material Type</p>
                  <div style={{
                    width:"200px",
                    margin:"auto"}}>
                      <Select isMulti
                      options={material}
                      getOptionValue={(option)=>option.value}
                      />


                  </div>
                </label>

                <label>
                  <p>Color</p>
                </label>

                <label>
                  <p>Quantity KG</p>
                </label>

                <label>
                  <p>Print Temp</p>
                </label>

                <label>
                  <p>Bed Temp</p>
                </label>


            </div>

        </div>
        <div>
            
        </div>
    </div>
  )
}
