    <div className='sign'>
        <UserNav/>

        <h1>Add Design</h1>
        <div className='SignIn'>
            
            <label>
                <p>FileHash</p>
                <input type="text" placeholder='filehash' onChange={(event)=> SetHash(event.target.value)}/>
            </label>

                <br/>
            <label>
                <p>Select STL File</p>
                <input type="file" name="file"  onChange={(event)=>onChangeFile(event)}/>
                <UploadSTL changeFile={(val) => {onChangeFile(val)}}/>
                <StlRenderer file={selectedFile}/>
                </label>
           <button className="printerButton" onClick={handleSubmit}>Add Design</button>

        </div>

    </div>
  )
}
