import React, { Component } from 'react';
import { StlViewer } from 'react-stl-file-viewer';

class StlRenderer extends Component{
    render(){
        const style = {
            top: 0,
            left: 0,
            width:'300px',
            height:'300px'
        }
        return (
        
            <StlViewer
                style={style} 
                url={this.props.file}
                groundColor='white'
                objectColor='rgb(50, 255, 255)'
                skyboxColor='white'
                gridLineColor='white'
                lightColor='white'
                volume={(val) => {this.setState({volume:val})}}
            /> 
        
        )
    }
}

export default StlRenderer;
